import React, {Component} from "react";
import memberInfo from "../Teams/memberInfo";
import TeamMember from "../Teams/TeamMember";

class LastWork extends Component {
    constructor() {
        super()
        this.state = {
            number : 0,
            memberMore : [],
            memberInfo : memberInfo,
        };
        this.clickMore = this.clickMore.bind(this);
    }
    
    clickMore() {
        memberInfo.map(member => <TeamMember
                                    id = {member.id}
                                    img = {member.img}
                                    name = {member.name}
                                    position = {member.position}
                                    phone = {member.phone}
                                    email = {member.email}
                                    website = {member.website}
                                 />
        );
        if (this.memberMore.leanght < memberInfo.leanght) {
            this.memberMore.push( memberInfo[this.state.number] )
        };
        this.setState(prevNum => {
            return {number : prevNum.number + 1}
        });
        console.log(this.memberMore);
    }    

    render() {
        return(
            <div>
                <button className="container bg-black text-white rounded" onClick={this.clickMore}>
                    More Of Our Team
                </button>
                <div>{this.memberMore}</div>
            </div>
        )
    }
}

export default LastWork;
